import {createReducer, createAsyncThunk,} from "@reduxjs/toolkit"
import axios from "axios"

export const getCategories = createAsyncThunk("GET_CATEGORIES", () => {
    console.log("GET_CATEGORIES")
    return axios.get(`/api/categories`).then(res => res.data)
})

export const setNewCategory = createAsyncThunk("SET_NEW_CATEGORY", (name) => {
    console.log("SET_NEW_CATEGORY", name)
    return axios.post(`/api/categories`, { name })
})

export const removeCategory = createAsyncThunk("REMOVE_CATEGORY", () => {
    console.log("REMOVE_CATEGORY")
    return axios.delete(`/api/categories?id=${id}`)
})

export const editCategory = createAsyncThunk("EDIT_CATEGORY", (id) => {
    console.log("EDIT_CATEGORY")
    return axios.put(`/api/categories?id=${id}`)
})



const categoriesReducer = createReducer({}, {
    [setNewCategory.fulfilled] : (state, action) => action.payload,
    [removeCategory.fulfilled] : (state, action) => action.payload,
    [editCategory.fulfilled] : (state, action) => action.payload
})

export default categoriesReducer
