import React from 'react'
import { useDispatch, useSelector } from "react-redux";
import { getCategories, 
        setNewCategory, 
        removeCategory,
        editCategory 
    } from '../../store/categories'

export default function Categorias() {

    const dispatch = useDispatch()
    const [categoryName, setcategoryName] = React.useState("")
    let categories = useSelector(state => state.categories)

    React.useEffect(() => {
        dispatch(getCategories())
    }, [dispatch])

    const handlePut = (category) => {
        dispatch(editCategory(category.id))
    }

    const handleRemove = (category) => {
        dispatch(removeCategory(category.id))
    }

    const handleAdd = () => {
        dispatch(removeCategory(categoryName))
    }


    return (
        <div className='categories-list'>
            <h3>Categorias:</h3>
            {!categories.length ? (<h4>No se encontraron categorías</h4>)
            :
            categories.map((category, i) => 
            <div key={i}>
                <li>{category.name}</li>
                <button onClick={category => handlePut(category)} className='log-btn'>Editar</button>
                <button onClick={category => handleRemove(category)} className='log-btn'>Eliminar</button>
            </div>
            )}
            <br/>
            <h5>Nueva categoría:</h5>
            <input placeholder="Nombre de categoría" onChange={data => setcategoryName(data)}/>
            <button className='buy-btn' type='button' categoryName={categoryName} onClick={handleAdd}>Añadir</button>
        </div>
    )
}
