import * as React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from 'react-router-dom'
import axios from 'axios'
import { setProductos, buscarProducto } from '../store/productos'

export default function ProductList() {


const dispatch = useDispatch()
let productos = useSelector(state => state.productos)
let item = useSelector(state => state.buscarProducto)

function shuffleArray(array) {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
    return array
}

const buscar = function(e) {
    if(e.keyCode === 13){
        item = e.target.value
        dispatch(buscarProducto(item))
    }
}

React.useEffect(() => {
    axios.get('api/productos')
        .then((res) => res.data)
        .then(data => { dispatch(setProductos(data)) })
},[])

    return(
        <div>
            <input placeholder='Buscar item...' onKeyUp={buscar}></input>
            {productos.length ? 
                     console.log("Producto: ", shuffleArray(productos))
                :
                "No se encontraron productos."
            }
            <h1>Productlist</h1>
        </div>
    )
}
