/** @format */
import * as React from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { setProductos, buscarProducto } from "../store/productos";
import axios from "axios";
import { ListGroup } from "react-bootstrap";
import "../assets/index.css"

export default function Sidebar() {

  const dispatch = useDispatch();

  const handleFilter = (item) => {
    axios
      .get(`/api/products?item=${item}`)
      .then((res) => res.data)
      .then((data) => {
          dispatch(setProductos(data));
      });
  }

  return (
    <div className="sideBar">
      <ListGroup defaultActiveKey="">
        <ListGroup.Item className='categories'>
         <h5 >Categor√≠as</h5>
        </ListGroup.Item>
        <button className='categories-item' onClick={handleFilter('pc')}>
          PCs completas
        </button>
        <ListGroup.Item action href="#link2" enable>
          Laptops
        </ListGroup.Item>
        <ListGroup.Item action href="#link3">
          Monitores
        </ListGroup.Item>
        <ListGroup.Item action href="#link4" enable>
          Teclados
        </ListGroup.Item>
        <ListGroup.Item action href="#link5">
          Mouses
        </ListGroup.Item>
        <ListGroup.Item action href="#link6" enable>
          Componentes
        </ListGroup.Item>
        <ListGroup.Item action href="#link7">
          Auriculares
        </ListGroup.Item>
        <ListGroup.Item action href="#link8" enable>
          Cables y conectores
        </ListGroup.Item>
        <ListGroup.Item action href="#link9" enable>
          Impresoras
        </ListGroup.Item>
        <ListGroup.Item action href="#link10" enable>
          Tablets
        </ListGroup.Item>
        <ListGroup.Item action href="#link11" enable>
          Otros
        </ListGroup.Item>
      </ListGroup>
    </div>
  );
}
